package kontrollstrukturen;

public class SwitchAnweisung {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		final int konstante2 = 2; //  Konstante unter Java
		
		char var = 1;
		switch (var){
		case 1:
			break;
		case konstante2:
			break;
		default:
			break;
		}
	}

}
